document.addEventListener('DOMContentLoaded',function(){const container=document.querySelector('.gateway-container');container.style.opacity='0';container.style.transform='scale(0.9)';setTimeout(()=>{container.style.transition='all 1s ease';container.style.opacity='1';container.style.transform='scale(1)';},100);const enterButton=document.querySelector('.enter-button');enterButton.addEventListener('mouseenter',function(){this.style.animation='pulse 0.5s ease';});enterButton.addEventListener('animationend',function(){this.style.animation='';});});function enterSite(){const container=document.querySelector('.gateway-container');const audio=new Audio();container.style.transition='all 0.8s ease';container.style.transform='scale(1.1)';container.style.opacity='0';setTimeout(()=>{window.location.href='main.html';},800);}document.addEventListener('keydown',function(event){if(event.code==='Enter'||event.code==='Space'){event.preventDefault();enterSite();}});function createLightningEffect(){const overlay=document.querySelector('.background-overlay');overlay.style.transition='opacity 0.1s ease';overlay.style.opacity='0.8';setTimeout(()=>{overlay.style.opacity='0.3';},100);}setInterval(()=>{if(Math.random()<0.1){createLightningEffect();}},5000);